import React from "react";
import Header from "./Header";
import Order from "./Order";
import Inventory from "./Inventory";

/*
This app was created during "React for Beginners" -course by Wes Bos.

Order is for the middle column of the market page.
It holds a title and user's order from the market place.
*/

class App extends React.Component {

    state = {
        fishes: {},
        order: {}
    }
    addFish = fish => {

        // takes a copy of the existing state
        const fishes = { ...this.state.fishes };

        // adds our new fish to that fishes variable
        fishes[`fish${Date.now()}`] = fish;

        // sets the new fishes object to state
        this.setState({ fishes });
    };

    render() {
        return (
            <div className="catch-of-the-day">
                <div className="menu">
                    <Header tagline="Fresh Seafood Market" age={100}/>
                </div>
                <Order />
                <Inventory addFish={this.addFish} />
            </div>
        );
    }


}

export default App;
