import React from "react";
import AddFishForm from "./AddFishForm";

/**
 This app was created during "React for Beginners" -course by Wes Bos.

 EditFishForm is used to edit the information of existing fish.
 In EditFishForm, user can type in new fish type's name, price, availability and description and even add an image of it.
 */

class EditFishForm extends React.Component {
    render() {
        return <div class="fish-edit">

            { /* name of the fish */ }
                <input name="name"
                       type="text"
                />

                { /* price of the fish */ }
                <input name="price"
                       type="int"
                />

                { /* status of the fish, if it's available or not, in a dropdown menu */ }
                <select name="status"
                        type="text"
                >
                    <option value="available">Fresh!</option>
                    <option value="unavailable">Sold out!</option>
                </select>

                { /* description about the fish */ }
                <textarea name="desc"></textarea>

                { /* image of the fish, if available */ }
                <input name="image"
                       type="text"
                />
        </div>
    }
}

export default EditFishForm;
