import React from 'react';
import { getFunName } from "../helpers";

/*
This app was created during "React for Beginners" -course by Wes Bos.

StorePicker is for the first page of the store application.
In StorePicker, user can type a shop they want to the text box and the program will send them to that location.

For example: Funny Cat Videos store will send user to "/store/funnycatvideos.
 */

class StorePicker extends React.Component {

    // create a ref for text box input
    myInput = React.createRef();

    // create an event to get to the chosen store
    goToStore = event => {

        // 1. Stop the form from submitting
        event.preventDefault();

        // 2. Get the text from that input
        const storeName = this.myInput.value.value;

        // 3. Change the page to /store/whatever-they-entered
        this.props.history.push(`/store/${storeName}`);
    }

    render() {
        return (

            // this form is to help users get to the wanted store page
            <form className="store-selector" onSubmit={this.goToStore}>
                <h2>Please Enter A Store</h2>

                {/* gives a funny random name for the text box */}
                <input type="text" ref={this.myInput} placeholder="Store Name" defaultValue={getFunName()}/>

                {/* button to go to /store/whatever-they-entered */}
                <button type="submit">Visit Store --></button>
            </form>
        );
    }
}

// exports the page
export default StorePicker;
